    def speak(self):
        language = 'en'
        news = ''
        news += 'title: '
        news += self.t1.toPlainText()
        news += '. '
        news += 'author: '
        news += self.l1.text()
        news += '. description: '
        news += self.t2.toPlainText()
        news += '. Thank You.:'

        myObj = gTTS(text = news, lang = language, slow = False)
        
        myObj.save("news.mp3")
        
        os.system("start news.mp3")

